define('lib/require.text!../templates/playerStatDists.html',[],function () { return '<div data-bind="dropdown: \'player-stat-dists\'"></div>\n\n<h1>Player Stat Distributions <span data-bind="newWindow: []"></span></h1>\n<p>More: <a data-bind="attrLeagueUrl: {href: [\'player_stats\', season]}">Main Stats</a> | <a data-bind="attrLeagueUrl: {href: [\'player_shot_locations\', season]}">Shot Locations</a></p>\n\n<p>These <a href="http://en.wikipedia.org/wiki/Box_plot">box plots</a> show the league-wide distributions of player stats for all active players in the selected season. Black plots are for this league and blue plots are from the 2009-2010 NBA season, for comparison. NBA data was generously provided by <a href="http://www.databasebasketball.com/stats_download.htm">databaseBasketball.com</a>. The five vertical lines in each plot represent the minimum of the scale, the minimum, the first <a href="http://en.wikipedia.org/wiki/Quartile">quartile</a>, the median, the third quartile, the maximum, and the maximum of the scale.</p>\n\n<table id="player-stat-dists">\n  <tbody></tbody>\n</table>';});

define('lib/require.text!../templates/playerStats.html',[],function () { return '<div data-bind="dropdown: \'player-stats\'"></div>\n\n<h1>Player Stats <span data-bind="newWindow: []"></span></h1>\n<p>More: <a data-bind="attrLeagueUrl: {href: [\'player_shot_locations\', season]}">Shot Locations</a> | <a data-bind="attrLeagueUrl: {href: [\'player_stat_dists\', season]}">Stat Distributions</a></p>\n\n<table class="table table-striped table-bordered table-condensed" id="player-stats">\n  <thead>\n    <tr><th colspan="6"></th><th colspan="3" style="text-align: center" title="Field Goals">FG</th><th colspan="3" style="text-align: center" title="Three-Pointers">3PT</th><th colspan="3" style="text-align: center" title="Free Throws">FT</th><th colspan="3" style="text-align: center" title="Rebounds">Reb</th><th colspan="8"></th></tr>\n    <tr><th>Name</th><th title="Position">Pos</th><th>Team</th><th title="Games Played">GP</th><th title="Games Started">GS</th><th title="Minutes">Min</th><th title="Made">M</th><th title="Attempted">A</th><th title="Percentage">%</th><th title="Made">M</th><th title="Attempted">A</th><th title="Percentage">%</th><th title="Made">M</th><th title="Attempted">A</th><th title="Percentage">%</th><th title="Offensive">Off</th><th title="Defensive">Def</th><th title="Total">Tot</th><th title="Assists">Ast</th><th title="Turnovers">TO</th><th title="Steals">Stl</th><th title="Blocks">Blk</th><th title="Personal Fouls">PF</th><th title="Points">Pts</th><th title="Player Efficiency Rating">PER</th><th title="Estimated Wins Added">EWA</th></tr>\n  </thead>\n</table>\n';});

define('lib/require.text!../templates/playoffs.html',[],function () { return '<div data-bind="dropdown: \'playoffs\'"></div>\n\n<h1>Playoffs <span data-bind="newWindow: []"></span></h1>\n\n<p data-bind="visible: !finalMatchups()">This is what the playoff matchups would be if the season ended right now.</p>\n\n<table class="table-condensed" width="100%">\n  <tbody>\n    <tr>\n      <td width="14.28%" data-bind="matchup: [0, 0]"></td>\n      <td rowspan="2" width="14.28%" data-bind="matchup: [1, 0]"></td>\n      <td rowspan="4" width="14.28%" data-bind="matchup: [2, 0]"></td>\n      <td rowspan="4" width="14.28%" data-bind="matchup: [3, 0]"></td>\n      <td rowspan="4" width="14.28%" data-bind="matchup: [2, 1]"></td>\n      <td rowspan="2" width="14.28%" data-bind="matchup: [1, 2]"></td>\n      <td width="14.28%" data-bind="matchup: [0, 4]"></td>\n    </tr>\n    <tr>\n      <td data-bind="matchup: [0, 1]"></td>\n      <td data-bind="matchup: [0, 5]"></td>\n    </tr>\n    <tr>\n      <td data-bind="matchup: [0, 2]"></td>\n      <td rowspan="2" data-bind="matchup: [1, 1]"></td>\n      <td rowspan="2" data-bind="matchup: [1, 3]"></td>\n      <td data-bind="matchup: [0, 6]"></td>\n    </tr>\n    <tr>\n      <td data-bind="matchup: [0, 3]"></td>\n      <td data-bind="matchup: [0, 7]"></td>\n    </tr>\n  </tbody>\n</table>\n';});

define('lib/require.text!../templates/roster.html',[],function () { return '<div data-bind="dropdown: \'roster\'"></div>\n\n<h1><span data-bind="text: team.region"></span> <span data-bind="text: team.name"></span> Roster <span data-bind="newWindow: []"></span></h1>\n<p>More: <a data-bind="attrLeagueUrl: {href: [\'team_finances\', abbrev]}">Finances</a> | <a data-bind="attrLeagueUrl: {href: [\'game_log\', abbrev, season]}">Game Log</a> | <a data-bind="attrLeagueUrl: {href: [\'team_history\', abbrev]}">History</a></p>\n\n<h3>Record: <span data-bind="recordAndPlayoffs: [abbrev, season, team.won, team.lost, team.playoffRoundsWon, \'noSeason\']"></span></h3>\n\n<p data-bind="visible: isCurrentSeason">\n  <span data-bind="text: numRosterSpots"></span> open roster spots<br>\n  Payroll: <span data-bind="currency: [payroll, \'M\']"></span><br>\n  Salary cap: <span data-bind="currency: [salaryCap, \'M\']"></span><br>\n  Profit: <span data-bind="currency: [team.profit, \'M\']"></span><br>\n  <span data-bind="visible: showTradeFor">Strategy: <span data-bind="text: team.strategy"></span></span>\n</p>\n\n<p data-bind="visible: editable">Drag and drop row handles to move players between the starting lineup (<span class="roster-starter">&#9632;</span>) and the bench (<span class="roster-bench">&#9632;</span>).</p>\n<p data-bind="visible: editable"><button class="btn btn-default" id="roster-auto-sort">Auto sort roster</button></p>\n\n<table class="table table-striped table-bordered table-condensed" id="roster">\n  <thead>\n    <tr><th data-bind="visible: editable"></th><th>Name</th><th title="Position">Pos</th><th>Age</th><th title="Overall Rating">Ovr</th><th title="Potential Rating">Pot</th><th data-bind="visible: isCurrentSeason">Contract</th><th title="Games Played">GP</th><th title="Minutes Per Game">Min</th><th title="Points Per Game">Pts</th><th title="Rebounds Per Game">Reb</th><th title="Assists Per Game">Ast</th><th title="Player Efficiency Rating">PER</th><th title="Playing Time Modifier" data-bind="visible: editable" style="text-align: center">PT <span class="glyphicon glyphicon-question-sign help-icon" id="help-roster-pt" data-placement="left"></span></th><th data-bind="visible: editable">Release <span class="glyphicon glyphicon-question-sign help-icon" id="help-roster-release" data-placement="left"></span></th><th data-bind="visible: showTradeFor">Trade For</th></tr>\n  </thead>\n  <tbody data-bind="foreach: players">\n    <tr data-bind="attr: {class: $index() === 4 ? \'separator\' : \'\', \'data-pid\': pid}">\n      <td class="roster-handle" data-bind="visible: $parent.editable"></td>\n      <td data-bind="playerNameLabels: [pid, name, injury, ratings.skills]"></td>\n      <td data-bind="text: pos"></td>\n      <td data-bind="text: age"></td>\n      <td data-bind="text: ratings.ovr"></td>\n      <td data-bind="text: ratings.pot"></td>\n      <td data-bind="visible: $parent.isCurrentSeason">\n        <span data-bind="currency: [contract.amount, \'M\']"></span> thru <span data-bind="text: contract.exp"></span>\n      </td>\n      <td data-bind="text: stats.gp"></td>\n      <td data-bind="round: [stats.min, 1]"></td>\n      <td data-bind="round: [stats.pts, 1]"></td>\n      <td data-bind="round: [stats.trb, 1]"></td>\n      <td data-bind="round: [stats.ast, 1]"></td>\n      <td data-bind="round: [stats.per, 1]"></td>\n      <td data-bind="if: $parent.editable">\n        <select class="form-control" data-bind="options: $root.ptModifiers, optionsText: \'text\', optionsValue: \'ptModifier\', value: ptModifier"></select>\n      </td>\n      <td data-bind="visible: $parent.editable">\n        <button class="btn btn-default btn-xs" data-action="release" data-bind="enable: canRelease">Release</button>\n      </td>\n      <td data-bind="visible: $parent.showTradeFor">\n        <form method="POST" style="margin: 0" data-bind="attrLeagueUrl: {action: [\'trade\']}">\n          <input type="hidden" name="pid" data-bind="attr: {value: pid}">\n          <button type="submit" class="btn btn-default btn-xs">Trade For</button>\n        </form>\n      </td>\n    </tr>\n  </tbody>\n</table>';});

define('lib/require.text!../templates/schedule.html',[],function () { return '<h1>Upcoming Schedule <span data-bind="newWindow: []"></span></h1>\n\n<ol data-bind="foreach: games">\n  <li><a data-bind="attrLeagueUrl: {href: [\'roster\', teams[0].abbrev]}"><span data-bind="text: teams[0].region"></span> <span data-bind="text: teams[0].name"></span></a>\n  <span data-bind="text: vsat"></span>\n  <a data-bind="attrLeagueUrl: {href: [\'roster\', teams[1].abbrev]}"><span data-bind="text: teams[1].region"></span> <span data-bind="text: teams[1].name"></span></a>\n</ol>\n';});

define('lib/require.text!../templates/standings.html',[],function () { return '<div data-bind="dropdown: \'standings\'"></div>\n\n<h1>Standings <span data-bind="newWindow: []"></span></h1>\n\n<!-- ko foreach: confs -->\n  <h2 data-bind="text: name"></h2>\n  <div class="row">\n    <div class="col-sm-9" data-bind="foreach: divs">\n      <table class="table table-striped table-bordered table-condensed">\n        <thead>\n          <tr><th width="100%" data-bind="text: name"></th><th>W</th><th>L</th><th>Pct</th><th>GB</th><th>Home</th><th>Road</th><th>Div</th><th>Conf</th><th>Streak</th><th>L10</th></tr>\n        </thead>\n        <tbody data-bind="foreach: teams">\n          <tr><td><a data-bind="attrLeagueUrl: {href: [\'roster\', abbrev, $parents[2].season]}, text: region() + \' \' + name()"></a></td><td data-bind="text: won"></td><td data-bind="text: lost"></td><td data-bind="roundWinp: winp"></td><td data-bind="text: gb"></td><td data-bind="text: wonHome() + \'-\' + lostHome()"></td><td data-bind="text: wonAway() + \'-\' + lostAway()"></td><td data-bind="text: wonDiv() + \'-\' + lostDiv()"></td><td data-bind="text: wonConf() + \'-\' + lostConf()"></td><td data-bind="text: streak"></td><td data-bind="text: lastTen"></td></tr>\n        </tbody>\n      </table>\n    </div>\n\n    <div class="col-sm-3 hidden-xs">\n      <table class="table table-striped table-bordered table-condensed">\n      <thead>\n        <tr><th width="100%">Team</th><th align="right">GB</th></tr>\n      </thead>\n      <tbody data-bind="foreach: teams">\n        <tr data-bind="attr: {class: $index() === 7 ? \'separator\' : \'\'}"><td><span data-bind="text: rank"></span>. <a data-bind="attrLeagueUrl: {href: [\'roster\', abbrev, $parents[1].season]}, text: region"></a></td><td align="right" data-bind="text: gb"></td></tr>\n      </tbody>\n      </table>\n    </div>\n  </div>\n<!-- /ko -->';});

define('lib/require.text!../templates/teamFinances.html',[],function () { return '<div data-bind="dropdown: \'team-finances\'"></div>\n\n<h1><span data-bind="text: team.region"></span> <span data-bind="text: team.name"></span> Finances <span data-bind="newWindow: []"></span></h1>\n<p>More: <a data-bind="attrLeagueUrl: {href: [\'roster\', abbrev]}">Roster</a> | <a data-bind="attrLeagueUrl: {href: [\'game_log\', abbrev]}">Game Log</a> | <a data-bind="attrLeagueUrl: {href: [\'team_history\', abbrev]}">History</a></p>\n\n<p class="clearfix">The current payroll (<strong data-bind="currency: [team.payroll, \'M\']"></strong>) is <span data-bind="text: aboveBelow.minPayroll"></span> the minimum payroll limit (<strong data-bind="currency: [minPayroll, \'M\']"></strong>), <span data-bind="text: aboveBelow.salaryCap"></span> the salary cap (<strong data-bind="currency: [salaryCap, \'M\']"></strong>), and <span data-bind="text: aboveBelow.luxuryPayroll"></span> the luxury tax limit (<strong data-bind="currency: [luxuryPayroll, \'M\']"></strong>). <span class="glyphicon glyphicon-question-sign help-icon" id="help-payroll-limits" data-placement="bottom"></span></p>\n\n<div class="row">\n  <div class="col-lg-3 col-md-3 col-sm-2">\n    <h4>Wins</h4>\n    <div id="bar-graph-won" class="bar-graph-small"></div><br><br>\n    <span class="clickover"><h4>Hype <span class="glyphicon glyphicon-question-sign help-icon" id="help-hype" data-placement="right"></span></h4></span>\n    <div id="bar-graph-hype" class="bar-graph-small"></div><br><br>\n    <h4>Region Population</h4>\n    <div id="bar-graph-pop" class="bar-graph-small"></div><br><br>\n    <h4>Average Attendance</h4>\n    <div id="bar-graph-att" class="bar-graph-small"></div>\n  </div>\n  <div class="col-lg-5 col-md-4 col-sm-4">\n    <h4>Revenue</h4>\n    <div id="bar-graph-revenue" class="bar-graph-large"></div><br><br>\n    <h4>Expenses</h4>\n    <div id="bar-graph-expenses" class="bar-graph-large"></div><br><br>\n    <h4>Cash</h4>\n    <div id="bar-graph-cash" class="bar-graph-medium"></div>\n  </div>\n  <div class="col-lg-4 col-md-5 col-sm-6">\n    <form method="POST" id="finances-settings" data-bind="attrLeagueUrl: {action: [\'team_finances\']}">\n      <h4>Revenue Settings <span class="glyphicon glyphicon-question-sign help-icon" id="help-revenue-settings" data-placement="bottom"></span></h4>\n      <p class="text-danger"></p>\n      <div class="row">\n        <div class="pull-left finances-settings-label">Ticket Price</div>\n        <div class="input-group input-group-sm pull-left finances-settings-field">\n          <span class="input-group-addon">$</span><input type="text" name="budget[ticketPrice]" class="form-control" disabled="disabled" data-bind="value: team.budget.ticketPrice.amount">\n        </div>\n        <div class="pull-left finances-settings-text">Leaguewide rank: #<span data-bind="text: team.budget.ticketPrice.rank"></span></div>\n      </div>\n      <p></p>\n      <h4>Expense Settings <span class="glyphicon glyphicon-question-sign help-icon" id="help-expense-settings" data-placement="bottom"></span></h4>\n      <p class="text-danger"></p>\n      <div class="row">\n        <div class="pull-left finances-settings-label">Scouting</div>\n        <div class="input-group input-group-sm pull-left finances-settings-field">\n          <span class="input-group-addon">$</span><input type="text" name="budget[scouting]" class="form-control" disabled="disabled" data-bind="value: team.budget.scouting.amount"><span class="input-group-addon">M</span>\n        </div>\n        <div class="pull-left finances-settings-text-small">Current spending rate: #<span data-bind="text: team.budget.scouting.rank"></span><br>Spent this season: #<span data-bind="text: team.expenses.scouting.rank"></span></div>\n      </div>\n      <div class="row">\n        <div class="pull-left finances-settings-label">Coaching</div>\n        <div class="input-group input-group-sm pull-left finances-settings-field">\n          <span class="input-group-addon">$</span><input type="text" name="budget[coaching]" class="form-control" disabled="disabled" data-bind="value: team.budget.coaching.amount"><span class="input-group-addon">M</span>\n        </div>\n        <div class="pull-left finances-settings-text-small">Current spending rate: #<span data-bind="text: team.budget.coaching.rank"></span><br>Spent this season: #<span data-bind="text: team.expenses.coaching.rank"></span></div>\n      </div>\n      <div class="row">\n        <div class="pull-left finances-settings-label">Health</div>\n        <div class="input-group input-group-sm pull-left finances-settings-field">\n          <span class="input-group-addon">$</span><input type="text" name="budget[health]" class="form-control" disabled="disabled" data-bind="value: team.budget.health.amount"><span class="input-group-addon">M</span>\n        </div>\n        <div class="pull-left finances-settings-text-small">Current spending rate: #<span data-bind="text: team.budget.health.rank"></span><br>Spent this season: #<span data-bind="text: team.expenses.health.rank"></span></div>\n      </div>\n      <div class="row">\n        <div class="pull-left finances-settings-label">Facilities</div>\n        <div class="input-group input-group-sm pull-left finances-settings-field">\n          <span class="input-group-addon">$</span><input type="text" name="budget[facilities]" class="form-control" disabled="disabled" data-bind="value: team.budget.facilities.amount"><span class="input-group-addon">M</span>\n        </div>\n        <div class="pull-left finances-settings-text-small">Current spending rate: #<span data-bind="text: team.budget.facilities.rank"></span><br>Spent this season: #<span data-bind="text: team.expenses.facilities.rank"></span></div>\n      </div>\n      <br>\n      <div class="row">\n        <div class="pull-left finances-settings-label">&nbsp;</div>\n        <div class="input-group input-group-sm pull-left finances-settings-field">\n          <button class="btn btn-large btn-primary" style="line-height: 1.5em">\n        </div>\n      </div>\n    </form>\n  </div>\n</div>\n<p class="clearfix"></p>\n\n<h2>Player Salaries</h2>\n\n<p>You can release or buy out players from <a data-bind="attrLeagueUrl: {href: [\'roster\']}">your roster</a>. Released players who are still owed money are <i>shown in italics</i>.</p>\n\n<table class="table table-striped table-bordered table-condensed" id="player-salaries">\n<thead>\n  <tr>\n    <th>Name</th>\n    <!-- ko foreach: salariesSeasons -->\n      <th data-bind="text: $data"></th>\n    <!-- /ko -->\n  </tr>\n</thead>\n<tbody>\n</tbody>\n<tfoot>\n  <tr>\n    <th>Totals</th>\n    <!-- ko foreach: contractTotals -->\n      <th data-bind="currency: [$data, \'M\']"></th>\n    <!-- /ko -->\n  </tr>\n</tfoot>\n</table>';});

define('lib/require.text!../templates/teamHistory.html',[],function () { return '<div data-bind="dropdown: \'team-history\'"></div>\n\n<h1><span data-bind="text: team.region"></span> <span data-bind="text: team.name"></span> History <span data-bind="newWindow: []"></span></h1>\n<p>More: <a data-bind="attrLeagueUrl: {href: [\'roster\', abbrev]}">Roster</a> | <a data-bind="attrLeagueUrl: {href: [\'team_finances\', abbrev]}">Finances</a> | <a data-bind="attrLeagueUrl: {href: [\'game_log\', abbrev]}">Game Log</a></p>\n\n<div class="row">\n  <div class="col-sm-3">\n    <h2>Seasons</h2>\n    <p style="-moz-column-width: 15em; -moz-columns: 15em; -webkit-columns: 15em; columns: 15em;">\n      <!-- ko foreach: history -->\n        <span data-bind="recordAndPlayoffs: [$root.abbrev, season, won, lost, playoffRoundsWon]"></span><br>\n      <!-- /ko -->\n    </p>\n  </div>\n  <div class="col-sm-9">\n    <h2>Players</h2>\n    <p>Players currently on your team are highlighted in green. Active players on other teams are highlighted in blue.</p>\n    <table class="table table-striped table-bordered table-condensed" id="team-history-players">\n    <thead>\n      <tr><th>Name</th><th title="Position">Pos</th><th title="Games Played">GP</th><th title="Minutes Per Game">Min</th><th title="Points Per Game">Pts</th><th title="Rebounds Per Game">Reb</th><th title="Assists Per Game">Ast</th><th title="Player Efficiency Rating">PER</th></tr>\n    </thead>\n    </table>\n  </div>\n</div>';});

